import React from 'react';
import Axios from "axios";

class MyForm extends React.Component {
  constructor(props) {
    super(props);
    //this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {
      name: '',
      email: '',
      phone: '',
      address: '',
      city: '',
      country: ''
    }


    onChangeName = (e: React.ChangeEvent < HTMLInputElement > ) => {
      this.setState({
        name: e.target.value
      });
    };

    onChangeEmail = (e: React.ChangeEvent < HTMLInputElement > ) => {
      this.setState({
        email: e.target.value
      });
    };

    onChangePhone = (e: React.ChangeEvent < HTMLInputElement > ) => {
      this.setState({
        phone: e.target.value
      });
    };

    onChangeAddress = (e: React.ChangeEvent < HTMLInputElement > ) => {
      this.setState({
        address: e.target.value
      });
    };

    onChangecity = (e: React.ChangeEvent < HTMLInputElement > ) => {
      this.setState({
        city: e.target.value
      });
    };

    onChangecountry = (e: React.ChangeEvent < HTMLInputElement > ) => {
      this.setState({
        country: e.target.value
      });
    };

    this.onSubmit = (e) => {
      alert("**********");
      e.preventDefault();
    }

  }


  render() {
    const {
      name,
      email,
      phone,
      address,
      city,
      country
    } = this.state;
    return ( <
      form onSubmit = {
        this.handleSubmit
      } >
      <
      input type = "text"
      name = "name"
      value = {
        name
      }
      placeholder = {
        'Enter your name'
      }
      onChange = {
        this.onChangeName
      }
      /> <
      br / >
      <
      input type = "text"
      label = "Email"
      name = "email"
      value = {
        email
      }
      placeholder = {
        'Enter your Email'
      }
      onChange = {
        this.onChangeEmail
      }
      /> <
      br / >
      <
      input type = "text"
      name = "phone"
      value = {
        phone
      }
      placeholder = {
        'Enter your phone number'
      }
      onChange = {
        this.onChange
      }
      /> <
      br / >
      <
      input type = "text"
      name = "address"
      value = {
        address
      }
      placeholder = {
        'Enter your Address'
      }
      onChange = {
        this.onChange
      }
      /> <
      br / >
      <
      input type = "text"
      name = "city"
      value = {
        city
      }
      placeholder = {
        'Enter your city'
      }
      onChange = {
        this.onChange
      }
      /> <
      br / >
      <
      input type = "text"
      name = "country"
      value = {
        country
      }
      placeholder = {
        'Enter your country'
      }
      onChange = {
        this.onChange
      }
      /> <
      br / >

      <
      button onClick = {
        this.onSubmit
      } >
      Submit <
      /button> < /
      form >
    );
  }
}
export default MyForm;
